    author: string,
    body: string,
    caption: string,
    chatId: string,
    chatName: string,
    fromMe: number
    id: string,
    isForwarded: number
    messageNumber: number,
    meta: string
    quotedMsgId: string,
    self: number,
    senderName: string,
    time: number
    type: string
}

// Inbox
const Inbox = () => {
    // const [emails] = useState<Array<EmailItems>>(mails);
