import React, { useState, useEffect } from 'react';
import { Row, Col } from 'react-bootstrap';

// components
import PageTitle from '../../../components/PageTitle';

import ChatUsers from './ChatUsers';
import ChatArea from './ChatArea';
// dummy data
import { USERS, ChatUserType } from './data';

// ChatApp
const ChatApp = () => {
    const [selectedUser, setSelectedUser] = useState<ChatUserType>(USERS[1]);
    const [chatUsers, setchatUsers] = useState<ChatUserTypes[]>();
    /**
     * On user change
     */
    const onUserChange = (user: ChatUserType) => {
        setSelectedUser(user);
    };
    const token = "3px6cypvje3xsjmc";
    const instance = "7827";
    /**
     * get emails from your whatsapp
     */
    const fetchemailurl = "https://api.chat-api.com/instance7827/messages?token=3px6cypvje3xsjmc";
    
    useEffect(() => {
        console.log(66);
        fetchData()
    },[])

    
    const fetchData = () => {
        fetch(fetchemailurl)
        .then((res)=> res.json())
        .then((json)=>{
            let total = json.messages;
            total.sort((a : any,b : any) => b.time - a.time);
            console.log(total);
            let tempdata = [];
            let userdata : ChatUserType[] = [];

            for (let i = 0; i < total.length; i++) {
                if (tempdata.indexOf(total[i]['chatId']) == -1) {
                    tempdata.push(total[i]['chatId']);
                    let user : ChatUserType = {
                        id : i,
                        name : total[i]['chatName'],
                        avatar : '',
                        lastMessage : 'Hi,there',
                        totalUnread : 3,
                        lastMessageOn : '',
                        userStatus : 'online'
                    };
                    userdata.push(user)
                }
            }
            console.log(total);
            setchatUsers(userdata);
            // setEmailList([...display]);
            // setotherEmails([...total]);
            // settotalEmails(total.length);
        })
    }
    return (
        <>
            <PageTitle
                breadCrumbItems={[
                    { label: 'Apps', path: '/apps/chat' },
                    { label: 'Chat', path: '/apps/chat', active: true },
                ]}
                title={'Chat'}
            />

            <Row>
                <Col lg={5} xxl={3}>
                    <ChatUsers onUserSelect={onUserChange} />
                </Col>
                <Col lg={7} xxl={9}>
                    <ChatArea selectedUser={selectedUser} />
                </Col>
            </Row>
        </>
    );
};

export default ChatApp;
