const token = "3px6cypvje3xsjmc";
const instance = "7827";
/**
 * get emails from your whatsapp
 */

const getmeInfoUrl = "https://api.chat-api.com/instance7827/me?token=3px6cypvje3xsjmc";
const getAccountstatusUrl = "https://api.chat-api.com/instance7827/status?token=3px6cypvje3xsjmc";

const getmeInformation = () => {
    let meInfo;
    fetch(getmeInfoUrl)
    .then((res)=> res.json())
    .then((json)=>{
        meInfo = json;
    })
    return meInfo;
}

const getAccountstatus = () => {
    fetch(getAccountstatusUrl)
    .then((res) => res.json())
    .then((json)=>{
        let accountstatus = json;
        return accountstatus;
    })
}

export default getmeInformation;
